import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import Confetti from "react-confetti";

const Stage7 = () => {
  const [windowDimension, setWindowDimension] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });
  const [showConfetti, setShowConfetti] = useState(true);

  const detectSize = () => {
    setWindowDimension({
      width: window.innerWidth,
      height: window.innerHeight,
    });
  };

  useEffect(() => {
    window.addEventListener("resize", detectSize);
    // Stop confetti after 10 seconds
    const timer = setTimeout(() => {
      setShowConfetti(false);
    }, 10000);

    return () => {
      window.removeEventListener("resize", detectSize);
      clearTimeout(timer);
    };
  }, []);

  const photos = [
    {
      id: 1,
      placeholder: "First Date Memory",
      description: "Lido Bayside - Where it all started",
    },
    {
      id: 2,
      placeholder: "Superblue Adventure",
      description: "The night you stole my heart",
    },
    {
      id: 3,
      placeholder: "Our Journey",
      description: "Building dreams together at FIU",
    },
    {
      id: 4,
      placeholder: "Forever Moments",
      description: "Every day with you is special",
    },
  ];

  return (
    <div className="max-w-5xl mx-auto px-4 relative">
      {showConfetti && (
        <Confetti
          width={windowDimension.width}
          height={windowDimension.height}
          recycle={false}
          numberOfPieces={200}
          colors={["#ec4899", "#f472b6", "#f9a8d4", "#fbcfe8", "#fce7f3"]}
        />
      )}

      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-center mb-12"
      >
        <motion.h1
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ delay: 0.5, duration: 0.8 }}
          className="font-handwritten text-6xl md:text-8xl text-gradient mb-6"
        >
          Happy 2 Years!
        </motion.h1>

        <motion.div
          animate={{
            rotate: [0, 10, -10, 0],
            scale: [1, 1.2, 1],
          }}
          transition={{ duration: 2, repeat: Infinity }}
          className="text-6xl mb-4"
        >
          🎉💕✨
        </motion.div>
      </motion.div>

      {/* Photo Gallery */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1, duration: 0.8 }}
        className="bg-white/80 backdrop-blur-lg rounded-3xl shadow-2xl p-8 md:p-12 border border-white/30 mb-8"
      >
        <h2 className="font-handwritten text-4xl text-romantic-600 text-center mb-8">
          Our Memory Lane 📸
        </h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {photos.map((photo, index) => (
            <motion.div
              key={photo.id}
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ delay: 1.5 + index * 0.2, duration: 0.6 }}
              className="bg-gradient-to-br from-romantic-50 to-cream-50 rounded-2xl p-6 border border-romantic-200 shadow-lg hover:shadow-xl transition-all duration-300"
            >
              <div className="w-full h-48 bg-romantic-200 rounded-lg flex items-center justify-center border-2 border-dashed border-romantic-300 mb-4">
                <div className="text-center">
                  <div className="text-4xl mb-2">📷</div>
                  <p className="font-elegant text-romantic-600 text-sm">
                    {photo.placeholder}
                  </p>
                </div>
              </div>
              <p className="font-elegant text-romantic-700 text-center text-sm">
                {photo.description}
              </p>
            </motion.div>
          ))}
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 2.5, duration: 0.8 }}
          className="text-center bg-gradient-to-r from-romantic-100 to-cream-100 rounded-2xl p-8 border border-romantic-200"
        >
          <div className="text-5xl mb-4">💌</div>
          <h3 className="font-handwritten text-3xl text-romantic-600 mb-4">
            Final Message
          </h3>
          <p className="font-elegant text-lg text-romantic-700 leading-relaxed mb-4">
            Happy 2 Years, my love. You're my forever, my best friend, and my
            greatest blessing. Let's keep building our dreams together.
          </p>
          <div className="text-romantic-600 font-handwritten text-xl">
            Forever yours,
            <br />
            Rickny ❤️
          </div>
        </motion.div>
      </motion.div>

      {/* Final Reward */}
      <motion.div
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 3, duration: 0.8 }}
        className="bg-white/80 backdrop-blur-lg rounded-3xl shadow-2xl p-8 md:p-10 border border-white/30 text-center"
      >
        <motion.div
          animate={{
            bounce: [0, -20, 0],
            rotate: [0, 5, -5, 0],
          }}
          transition={{ duration: 2, repeat: Infinity }}
          className="text-7xl mb-6"
        >
          🍫🪑
        </motion.div>

        <h3 className="font-handwritten text-4xl text-romantic-600 mb-4">
          One Last Surprise!
        </h3>

        <p className="font-elegant text-lg text-romantic-700 mb-6 leading-relaxed">
          Your favorite candy is waiting for you...
          <br />
          <span className="font-semibold text-romantic-600">
            check under your seat 😉
          </span>
        </p>

        <div className="bg-orange-100 border border-orange-300 rounded-2xl p-6 shadow-lg">
          <motion.div
            animate={{ scale: [1, 1.1, 1] }}
            transition={{ duration: 2, repeat: Infinity }}
            className="text-4xl mb-2"
          >
            🥜🍫
          </motion.div>
          <p className="font-elegant text-orange-800 font-semibold">
            Reese's are waiting for the sweetest girl! 💝
          </p>
        </div>
      </motion.div>

      {/* Floating Elements */}
      <div className="fixed inset-0 pointer-events-none overflow-hidden z-0">
        {Array.from({ length: 12 }, (_, i) => (
          <motion.div
            key={i}
            className="absolute text-4xl"
            initial={{
              x: Math.random() * window.innerWidth,
              y: window.innerHeight + 50,
            }}
            animate={{
              y: -100,
              x: Math.random() * window.innerWidth,
              rotate: 360,
            }}
            transition={{
              duration: Math.random() * 8 + 12,
              repeat: Infinity,
              delay: Math.random() * 5,
              ease: "linear",
            }}
          >
            {
              ["❤️", "💕", "💖", "✨", "🌟", "💝"][
                Math.floor(Math.random() * 6)
              ]
            }
          </motion.div>
        ))}
      </div>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 4, duration: 1 }}
        className="text-center mt-12 mb-8"
      >
        <p className="font-elegant text-romantic-600 text-lg">
          🎊 Congratulations! You've completed our anniversary scavenger hunt!
          🎊
        </p>
        <motion.div
          animate={{ scale: [1, 1.2, 1] }}
          transition={{ duration: 2, repeat: Infinity }}
          className="text-6xl mt-4"
        >
          👑💕👑
        </motion.div>
      </motion.div>
    </div>
  );
};

export default Stage7;
